// 3) 若還沒有，改用 $items() 從 Switch3 抓（注意 outputIndex）
function getFromSwitch(nodeName, outputs = [0,1,2,3]) {
  for (const outIdx of outputs) {
    // 先抓同一輪
    try {
      const same = $items(nodeName, outIdx, $runIndex);
      if (Array.isArray(same) && same.length) {
        for (const it of same) {
          const v = it?.json?.origin;
          if (typeof v === 'string') return v;
          if (Array.isArray(v)) {
            const h = v.find(x => typeof x?.json?.origin === 'string');
            if (h) return h.json.origin;
          }
        }
      }
    } catch (e) { /* 同輪可能不存在，忽略 */ }

    // 抓最近一次
    try {
      const last = $items(nodeName, outIdx);
      if (Array.isArray(last) && last.length) {
        for (const it of last) {
          const v = it?.json?.origin;
          if (typeof v === 'string') return v;
          if (Array.isArray(v)) {
