const data = items[0].json.body;
const messages = data.messages || [];

let systemPrompt = '';
const userAssistantPairs = [];
let lastUserMessage = '';
let tempPair = [];

// ÂÖàÊâæ system prompt
for (const msg of messages) {
  if (msg.role === 'system' && !systemPrompt) {
    systemPrompt = msg.content;
  }
}

// Êî∂ÈõÜ user/assistant ÊàêÂ∞çÁöÑÂ∞çË©±
for (const msg of messages) {
  if (msg.role === 'user') {
    // Â¶ÇÊûú‰∏ä‰∏ÄÁµÑÂè™Êúâ userÔºåÊ≤íÊúâ assistantÔºåÂÖàÂ≠òËµ∑‰æÜ
    if (tempPair.length > 0) {
      userAssistantPairs.push(tempPair);
    }
    tempPair = [`‰ΩøÁî®ËÄÖÔºö${msg.content}`];
    lastUserMessage = msg.content;
  } else if (msg.role === 'assistant') {
    tempPair.push(`AIÔºö${msg.content}`);
    userAssistantPairs.push(tempPair);
    tempPair = [];
  }
}
// Â¶ÇÊûúÊúÄÂæå‰∏ÄÁµÑÂè™Êúâ user Ê≤íÊúâ assistant
if (tempPair.length > 0) {
  userAssistantPairs.push(tempPair);
}

// Âè™ÂèñÊúÄËøë 5 ÁµÑ
const last5Pairs = userAssistantPairs.slice(-5);
const dialogue = last5Pairs.flat().join('\n');

// Ëß£ÊûêË´ãÊ±ÇÁõÆÁöÑ
let ReqPurpose = 'query'; // È†êË®≠ÁÇ∫‰∏ÄËà¨Êü•Ë©¢

if (lastUserMessage.trim().startsWith('###')) {
  if (lastUserMessage.includes('Generate a concise, 3-5 word title')) {
    ReqPurpose = 'title';
  } else if (lastUserMessage.includes('Generate 1-3 broad tags')) {
    ReqPurpose = 'tags';
  } else if (
    lastUserMessage.includes('Suggest 3-5 relevant follow-up questions') ||
    lastUserMessage.includes('"follow_ups"')
  ) {
    ReqPurpose = 'follow_ups';
  }
}

return [
  {
    json: {
      systemPrompt,
      dialogue,
      lastUserMessage,
      allMessages: messages,
      ReqPurpose // ÈÄôÂÄãÁµ¶ IF ÁØÄÈªûÂà§Êñ∑
    }
  }
];

const models = {
  object: "list",
  data: [
    {
      id: "Áü•Ë≠òÊ©üÂô®‰∫∫",
      object: "model",
      created: 1686935000,
      owned_by: "jun"
    }
  ]
};

return [
  {
    json: models
  }
];

// Ë§áÈõú Notion È†ÅÈù¢ÂÖßÂÆπÊï¥ÁêÜ
let content = '';
let pageTitle = $('Loop Over Items').first()?.json?.title ?? "";
let keywords = $('Loop Over Items').first()?.json?.Keywords ?? [];
if (!Array.isArray(keywords)) {
  keywords = [];
}
let gameServer = $('Loop Over Items').first()?.json?.GameServer ?? [];
if (!Array.isArray(gameServer)) {
  gameServer = [];
}
let system = $('Loop Over Items').first()?.json?.system ?? [];
if (!Array.isArray(system)) {
  system = [];
}

let sections = [];
let currentSection = '';

// ËôïÁêÜÊØèÂÄã block
for (const block of items) {
  const type = block.json.type;
  const data = block.json[type];

  // 1. Ê®ôÈ°åÂûãÂçÄÂ°äÔºàÈÅáÂà∞Ê®ôÈ°åÂ∞±ÈñãÂßãÊñ∞ÂçÄÂ°äÔºâ
  const textArr = data?.text || data?.rich_text || [];
  if (['heading_1', 'heading_2', 'heading_3'].includes(type)) {
    // ÂÖàÊääÁèæÊúâÂÖßÂÆπÊî∂ÈÄ≤ sections
    if (currentSection.trim()) {
      sections.push(currentSection.trim());
      currentSection = '';
    }
    const text = textArr.map(rt => rt.plain_text).join('').trim();
    if (text) {
      currentSection += `„Äê${text}„Äë\n`;
    }
    continue;
  }

  // 2. ‰∏ÄËà¨ÊÆµËêΩ„ÄÅÊ¢ùÂàó„ÄÅ‰ªªÂãôÊ∏ÖÂñÆ„ÄÅÂºïÁî®
  if (
    [
      'paragraph', 'bulleted_list_item', 'numbered_list_item', 'to_do', 'quote'
    ].includes(type)
  ) {
    const text = textArr.map(rt => rt.plain_text).join('').trim();
    if (text) {
      if (type.endsWith('list_item')) {
        currentSection += `„Éª${text}\n`;
      } else {
        currentSection += text + '\n';
      }
    }
    continue;
  }

  // 3. code ÂçÄÂ°ä
  if (type === 'code') {
    const codeText = textArr.map(rt => rt.plain_text).join('').trim();
    if (codeText) {
      currentSection += `\`\`\`\n${codeText}\n\`\`\`\n`;
    }
    continue;
  }

  // 4. table row
  if (type === 'table_row') {
    const rowCells = data.cells || [];
    const rowText = rowCells.map(
      cell => cell.map(rt => rt.plain_text).join('')
    ).join(' | ').trim();
    if (rowText) {
      currentSection += rowText + '\n';
    }
    continue;
  }

  // 5. image ÂçÄÂ°ä
  if (type === 'image') {
    const caption = (data.caption || []).map(rt => rt.plain_text).join('').trim();
    if (caption) {
      currentSection += `[ÂúñÁâáË™™ÊòéÔºö${caption}]\n`;
    } else {
      currentSection += `[ÂúñÁâá]\n`;
    }
    continue;
  }

  // 6. link_preview ÂçÄÂ°ä
  if (type === 'link_preview') {
    const url = data.url || '';
    if (url) {
      currentSection += `ÈÄ£ÁµêÔºö${url}\n`;
    }
    continue;
  }
}

// Êî∂Â∞æÔºåÊúÄÂæå‰∏ÄÂçÄ‰πüË¶ÅÂä†ÈÄ≤ sections
if (currentSection.trim()) {
  sections.push(currentSection.trim());
}

// Áî® --- ÂàÜÂçÄ
content = sections.join('\n---\n');

// Ê∫ñÂÇô keywords Âíå tags ÁöÑÊñáÂ≠óË°®Á§∫ÔºàÁî®ÊñºÊêúÂ∞ãÔºâ
const keywordText = keywords.length > 0 ? `ÈóúÈçµÂ≠óÔºö${keywords.join(', ')}` : '';
const gameServerText = gameServer.length > 0 ? `ÈÅäÊà≤‰º∫ÊúçÂô®Ôºö${gameServer.join(', ')}` : '';
const systemText = system.length > 0 ? `Á≥ªÁµ±Ôºö${system.join(', ')}` : '';

// ÁµÑÂêà pageContentÔºàÁµ¶ embedding ‰ΩøÁî®ÁöÑÂÆåÊï¥ÊñáÂ≠óÔºâ
let pageContent = `Ê®ôÈ°åÔºö${pageTitle}\n`;
if (keywordText) pageContent += keywordText + '\n';
if (gameServerText) pageContent += gameServerText + '\n';
if (systemText) pageContent += systemText + '\n';
pageContent += `\nÂÖßÂÆπÔºö\n${content}`;

// ÂèñÂæóÈ†ÅÈù¢ID
const pageId = items[0]?.json.parent?.page_id || items[0]?.json.id;

// ÂõûÂÇ≥ Qdrant Vector Store ÈúÄË¶ÅÁöÑÊ†ºÂºè
return [
  {
    json: {
      // pageContent ÊòØÁî®ÊñºÂêëÈáèÂåñÁöÑÂÆåÊï¥ÊñáÂ≠ó
      pageContent: pageContent,

      // metadata ÂåÖÂê´ÊâÄÊúâÁµêÊßãÂåñË≥áÊñô
      metadata: {
        id: pageId,
        title: pageTitle,
        pageId: pageId,
        keywords: keywords,
        gameServer: gameServer,
        system: system,
        content: content,
        sections: sections, // ‰øùÁïôÂàÜÊÆµË≥áË®ä
        sectionCount: sections.length,
        contentLength: content.length,
        source: "notion",
        contentType: "knowledge_base",
        indexed_at: new Date().toISOString(),
        // ÊêúÂ∞ãÂèãÂñÑÁöÑÊ®ôÁ±§
        searchTags: [...keywords, ...gameServer, ...system].filter(tag => tag && tag.trim()),
        // ÊòØÂê¶ÂåÖÂê´Á®ãÂºèÁ¢º
        hasCode: content.includes('```'),
        // ÊòØÂê¶ÂåÖÂê´Ë°®Ê†º
        hasTable: content.includes('|'),
        // ÊòØÂê¶ÂåÖÂê´ÂúñÁâá
        hasImage: content.includes('[ÂúñÁâá')
      }
    }
  }
];

// items ÊòØ n8n Ê®ôÊ∫ñÁöÑËº∏ÂÖ•ÔºåÊØè‰∏ÄÁ≠ÜÂ∞±ÊòØ‰∏ÄÂÄãË¶èÂâáÈ†Å

return items.map(item => {
  // È†ÅÈù¢ id
  const pageId = item.json.id;

  // È†ÅÈù¢Ê®ôÈ°åÔºàtitle Ê¨Ñ‰ΩçÊòØÂÄãÈô£ÂàóÔºâ
  let title = '';
  const titleArr = item.json.properties?.È†ÖÁõÆ?.title || [];
  if (titleArr.length > 0) {
    title = titleArr.map(t => t.plain_text).join('');
  }

  // Notion È†ÅÈù¢Á∂≤ÂùÄÔºà‰Ω†Â∑≤Á∂ìÊúâ url Ê¨Ñ‰ΩçÔºâ
  const url = item.json.url;

  const system = (item.json.properties?.Á≥ªÁµ±?.multi_select || []).map(s => s.name);
  const lastEditedTime = item.json.last_edited_time;

  return {
    json: {
      pageId,
      title,
      url,
      system,
      lastEditedTime
    }
  };
});

const embeddingResult = $input.first().json;
const processedData = $('Êï¥ÁêÜÂñÆ‰∏ÄÈ†ÅÈù¢').first().json;
const vector = embeddingResult.embedding.values;

console.log('=== Building Qdrant Point ===');
