                    metadata: {
                        title: processedData.metadata.title,
                        pageId: processedData.metadata.pageId,
                        keywords: processedData.metadata.keywords,
                        system: processedData.metadata.system,
                        source: processedData.metadata.source,
                        contentType: processedData.metadata.contentType,
                        contentLength: processedData.metadata.contentLength,
                        sectionCount: processedData.metadata.sectionCount,
