          role: "assistant",
          content: $input.first().json.output.answer
        },
        finish_reason: "stop"
      }
    ],
    usage: {
      prompt_tokens: 50,
      completion_tokens: 100,
      total_tokens: 150
    }
  }
}];

// ÂèñÂæóÁï∂ÂâçÁöÑ chat Ëº∏ÂÖ•
const currentInput = $input.first().json;
const chatInput = currentInput.chatInput || '';
const sessionId = currentInput.sessionId || '';

// ÂòóË©¶ÂèñÂæóÂ∞çË©±Ê≠∑Âè≤ (Â¶ÇÊûúÊúâÊé• Memory ÁØÄÈªû)
let chatHistory = [];
try {
  // Â¶ÇÊûú‰Ω†ÊúâÊé• Window Buffer Memory ÊàñÂÖ∂‰ªñ Memory ÁØÄÈªû,ÊîπÊàêÂ∞çÊáâÁöÑÁØÄÈªûÂêçÁ®±
  chatHistory = $('Window Buffer Memory').all();
} catch (e) {
  // Ê≤íÊúâ Memory ÁØÄÈªû‰πüÊ≤íÈóú‰øÇ
  console.log('Ê≤íÊúâÊâæÂà∞ Memory ÁØÄÈªû,‰ΩøÁî®Á©∫Ê≠∑Âè≤');
}

let systemPrompt = '';
const userAssistantPairs = [];
let lastUserMessage = chatInput;
let tempPair = [];
const allMessages = [];

// ËôïÁêÜÊ≠∑Âè≤Ë®äÊÅØ
if (chatHistory && chatHistory.length > 0) {
  for (const item of chatHistory) {
    const message = item.json;

    // Memory ÁØÄÈªûÁöÑÊ†ºÂºèÂèØËÉΩÊòØ type: 'human'/'ai' Êàñ role: 'user'/'assistant'
    const role = message.type === 'human' ? 'user' 
                : message.type === 'ai' ? 'assistant' 
                : message.role || 'user';
    const content = message.text || message.content || message.message || '';

    if (!content) continue;

    allMessages.push({ role, content });

    if (role === 'system' && !systemPrompt) {
      systemPrompt = content;
      continue;
    }

    if (role === 'user') {
      if (tempPair.length > 0) {
        userAssistantPairs.push(tempPair);
      }
      tempPair = [`‰ΩøÁî®ËÄÖ:${content}`];
    } else if (role === 'assistant') {
      tempPair.push(`AI:${content}`);
      userAssistantPairs.push(tempPair);
      tempPair = [];
    }
  }
}

// Âä†ÂÖ•Áï∂Ââç‰ΩøÁî®ËÄÖË®äÊÅØ
if (lastUserMessage) {
  allMessages.push({ role: 'user', content: lastUserMessage });

  // Â¶ÇÊûúÂâçÈù¢ÊúâÊú™ÂÆåÊàêÁöÑ pair,ÂÖàÂ≠òËµ∑‰æÜ
  if (tempPair.length > 0) {
    userAssistantPairs.push(tempPair);
  }
  // Âä†ÂÖ•Áï∂Ââç‰ΩøÁî®ËÄÖË®äÊÅØ
  userAssistantPairs.push([`‰ΩøÁî®ËÄÖ:${lastUserMessage}`]);
}

// Âè™ÂèñÊúÄËøë 5 ÁµÑ
const last5Pairs = userAssistantPairs.slice(-5);
const dialogue = last5Pairs.flat().join('\n');

// Ëß£ÊûêË´ãÊ±ÇÁõÆÁöÑ
let ReqPurpose = 'query';
if (lastUserMessage.trim().startsWith('###')) {
  if (lastUserMessage.includes('Generate a concise, 3-5 word title')) {
    ReqPurpose = 'title';
  } else if (lastUserMessage.includes('Generate 1-3 broad tags')) {
    ReqPurpose = 'tags';
  } else if (
    lastUserMessage.includes('Suggest 3-5 relevant follow-up questions') ||
    lastUserMessage.includes('"follow_ups"')
  ) {
    ReqPurpose = 'follow_ups';
  }
}

// Ëº∏Âá∫ÁµêÊûú
return {
  json: {
    sessionId,           // ‰øùÁïô sessionId Áµ¶ÂæåÁ∫åÁØÄÈªû‰ΩøÁî®
    systemPrompt,
    dialogue,
    lastUserMessage,
    allMessages,
    ReqPurpose,
    chatInput            // ‰øùÁïôÂéüÂßã chatInput
  }
};
