// 取得輸入資料（假設是從上一節點傳來的 JSON 陣列）
const items = $input.all();

// 轉換每一筆資料
return items.map(item => {
  return {
    json: {
      newsContent: item.json.newsContent,
      newsTitle: item.json.newsTitle,
      remind: item.json.remind
    }
  };
});

const items = $input.all();

for (const item of items) {
  if (item.json && item.json.newsContent) {
    let content = item.json.newsContent;

    // 移除所有 HTML 標籤
    content = content.replace(/<[^>]*>/g, '');

    // 轉義雙引號改為 &quot; （如果需要）
    content = content.replace(/"/g, '&quot;');

    // 也可以視需要加上其他轉義，這裡先不做換行、tab等轉義
    item.json.newsContent = content.trim();
  }
}

return items;

const candidates = $input.first()?.json?.candidates;
let text = candidates?.[0]?.content?.parts?.[0]?.text ?? null;

// 如果有值就移除換行符號
if (text) {
  text = text.replace(/\n/g, '');
}

return [
  {
    json: { text}
  }
];

const item = $input.all()[0].json;

