  // --- 前次標題 prompt（容錯多路徑）---
  const prevTitlePrompt =$('Merge').first().json.prompt
 ;

  // 其他欄位
  const typeValue = String(src.weather_report_type ?? "");
  const weatherReportContent = String(src.weather_report_report_content ?? "");

  // === 你指定要「一定包含」的 Gatekeeper 產標題 Prompt ===
  const titleGatekeeperPrompt = `


內文：

${weatherReportContent}
`;

  // 輸出：保留「全部 input 欄位」，再補上需要的欄位
  return {
    json: {
      // 先展開原始輸入（把 input 都「加入」）
      ...src,

      // 你要的輸出欄位（不覆寫 src.title；type/ weather_report/ prompt 另外補上）
      type: typeValue,
      weather_report: weatherReportContent,
      prompt: titleGatekeeperPrompt,

      // 額外撈取資訊
      prev_title: prevTitleValue,
      prev_validation_result: prevValidationResult,
      prev_validation_reason: prevValidationReason,
      prev_title_prompt: prevTitlePrompt,
    },
  };
});

