return $items().map(item => {
  // 先取 content 欄位
  const content = item.json["content"] || "";

  // 將 Markdown 粗體與換行符號轉 HTML
  const html = content
    .replace(/\\n/g, "<br>")                       // 換行符號
    .replace(/\*\*(.*?)\*\*/g, "<strong>$1</strong>"); // 粗體

  return {
    json: {
      ...item.json, // 保留原本欄位
      content_html: html, // 新增 HTML 欄位
    }
  };
});

const inputText =$input.first().json.output ;

// 找出 ```json ... ``` 區塊
const regex = /```json\s*([\s\S]*?)```/;
const match = inputText.match(regex);
if (!match) throw new Error("找不到 JSON 格式區塊");

// 取出內容
const jsonBlock = match[1];

// 手動抓 title 與 content
// 假設 title 以 "title": " 開頭，content 以 "content": " 開頭
const titleStart = jsonBlock.indexOf('"title": "') + 10;
const titleEnd = jsonBlock.indexOf('",', titleStart);
const contentStart = jsonBlock.indexOf('"content": "') + 12;
const contentEnd = jsonBlock.lastIndexOf('"'); // 最後一個雙引號

const title = jsonBlock.substring(titleStart, titleEnd);
const content = jsonBlock.substring(contentStart, contentEnd);

// 輸出
return [{
  json: {
    prompt: jsonBlock, // 原始 JSON 區塊，保留
    title: title,
    content: content
  }
}];

